function y = Evaluate(x,func_num)
[m,n] = size(x);
c = n;
if func_num == 1
    shift = [23.2272030228426,-19.6186830273006,-5.85189330186293,4.02476497849354,-25.8351232652321,-8.22096813324685,-4.98659761939961,16.6045588307119,11.7637393122884,0.804428807989641,19.7936435157796,-24.2852815220462,6.50748960520246,8.92957036480340,-21.9587125620024,-17.1843639502202,10.4123495034857,-29.4878508511649,26.6551279497567,26.9457698156373,27.0023721204871,-5.47468027522719,-9.66497784114566,-9.46048005207426,1.97002018471744,4.60955498326618,1.58519015199181,22.3342668973410,21.9336617226248,8.42914936389137];
    x = x-shift(1:n);
    y = (sum(x.^2, 2));
elseif func_num == 2
    shift = [26.1743014153353,-8.43596209637331,-27.2341608782950,-24.8880703734752,4.10636663163032,24.3973222372524,23.8950987454743,13.4035511421989,8.33159212224802,10.1962941317395,22.9943823844016,-9.97038960405204,8.15107881896908,-13.5005494736423,-26.7737739006173,-11.9437689287440,-26.8337873955764,-3.36348205939844,-3.05647869783111,17.0166775291189,-20.1065847295274,-17.6264946854336,-26.6052962811887,0.592708429976575,-29.9086721352658,20.2861671743529,-1.10030064291447,-10.6605814789070,27.1224575918104,-17.3957541331258];
    x = x - shift(1:n);
    x_in = [1:n].*x.^2;
    y = (sum(x_in, 2));
elseif func_num == 3
%     shift = [-13.6624867488639,-24.7585174268371,-7.84225518670159,-11.2606310486875,-23.3843752056151,-16.4973823462281,-16.2472241998368,20.9560275969343,-29.7651043701237,16.1520503605072,-16.6961842359573,-21.6249792645381,-28.1974162956041,-29.8969046316381,-16.3684279849812,-24.0703545326875,-7.11178756608009,-29.4074815423251,3.25541208016352,0.774942218768857,-17.4879032138255,14.6762913043358,-5.86425152485946,8.02962477440262,28.3855321693110,-13.6360906310050,12.7255394836916,28.8210982894778,-3.17890659337116,17.3888394171197];
    a = 20;
    b = 0.2;
    cc = 2*pi;
    c = n;
    POP = 3*(x)/5;%-shift(1:n)
    y = (0-a*exp(0-b*(mean(POP(:,1:c).^2,2)).^0.5)-exp(mean(cos(cc*POP(:,1:c)),2))+a+exp(1));
elseif func_num == 4
    shift = [16.7956092516780,0.610383180751029,5.10656394990939,24.7082461555349,13.2550696650534,8.01849440677848,23.7716309435748,-22.5205430423113,24.5541871760922,22.1755713486338,-11.4644103888734,-3.53675208369781,-9.73370549699979,-25.7940172798260,20.6947649289059,6.05561900305777,-26.0790707863038,4.87285305974877,7.85013441293796,29.3784756225249,-25.4548969941442,-8.33267488025215,-27.2670854503433,-12.6688575990264,15.8001630803162,6.89002319292822,-6.36860563654728,-17.9826892160879,-9.20394383282225,5.41144956059419];
    x = x - shift(1:n);
    POP = 0.1*x;
    y=(10*c+sum(POP(:,1:c).^2-10*cos(2*pi*POP(:,1:c)),2));
elseif func_num == 5
    shift = [-26.2537740866629,24.8448036796892,-6.55830191590015,14.8731941852720,2.73999873484817,21.4264147483969,23.5718531407842,-1.08012743052774,-28.5136310714814,25.7004896164909,-10.8365903172636,8.98780423611213,-8.28925817914509,7.63562252650318,10.3720111071702,2.12580715242683,-9.76509965091722,-11.2923659481353,11.9265058337732,-1.58768289530367,16.4238534728544,-8.97688297931460,14.9630645386328,-17.6274255271954,-21.5258848278124,-6.58383171420084,-2.10747508017882,22.5239307411593,-7.78991522269668,-29.6439504954053];
    x = x - shift(1:n);
    POP = x*12;
    P=[1:c].^0.5;
    y=(sum(POP(:,1:c).^2,2)/4000+1-prod(cos(POP(:,1:c)./P),2));
elseif func_num == 6
    shift = [4.88821702833307,-14.1584319323116,-25.3469261275086,-4.75045591709262,-13.4479204098998,-16.7935367749830,-4.47138639615563,29.0770991259037,23.5923482809372,-7.07024114069822,5.83828653601724,-22.7922614774728,-27.9210820255295,-28.7485882602618,19.9721044881431,7.99785820730918,-26.9848859104380,3.29187476046848,-25.0695926109670,-7.14437425584939,26.0847796571625,-10.3671598461292,-10.0852609857577,22.8123687570230,-22.3614898927087,21.9161549244748,13.1685948567998,-24.6122683468630,17.0634749606288,-0.865442823351959];
    x = x - shift(1:n);
    POP = x/25;
    P=100*(POP(:,2:c)-POP(:,1:c-1).^2).^2;
    y=(sum(P,2)+sum((POP(:,1:c-1)-1).^2,2));
end
